#task 3


def brute():
    n=0

    for a in range(10):
        start = time.perf_counter_ns()

        while hex(n) != arr[a]:
            n = n + 1

        if hex(n) == arr[a]:
            finish = time.perf_counter_ns()
            duration_programm = finish - start
            print("keysize", bits[a], "find bit:", hex(n), "\ntime found: ", duration_programm//1000000, " milisec", "\n")
            a = a + 1
brute()
